import React from "react";
import Section from "./Section";
import styled from 'styled-components';

const Text = styled.span`  
font-size: 3em;  color: black; font-weight: 700;  `;

const Container = styled.span` 
display: flex;
flex-direction: row;
flex-wrap: wrap; 
  `;

const Form = styled.form`
  display: flex;
  justify-content: space-between;
  align-items: center;
  border: 1px solid #e0e0e0;
  border-radius: 5px;
  background-color: #f2f2f2;
  text-align: right;

  @media (max-width: 800px) {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border: 1px solid #e0e0e0;
    border-radius: 5px;
    background-color: #f2f2f2;
    text-align: right;
  }
`;

const Input = styled.input`
  display: flex;
  background-color: #f2f2f2;
  padding: 20px 15px;
  border: none;
  font-size: 1rem;

  ::placeholder,
  ::-webkit-input-placeholder {
    font-size: 1.2rem;
    font-weight: 300;
    color: #000000;
  }

  :focus {
    outline: none;
  }

  :focus::placeholder {
    color: transparent;
  }
`;

const Select = styled.select`
  border: 0px solid #f2f2f2;
  border-radius: 5px;
  background-color: #f2f2f2;
  text-align: right;
  width: 100px;
  font-size: var(--text-prettysmall);

  :focus {
    outline: none;
  }
`;

const Option = styled.option`
  background-repeat: no-repeat;
  background-position: cover;
  padding-left: 0px;
`;


function Conversation (props) {
  return (
<Section color={props.color} size={props.size}>
  <div className="container">
   <Container> 
  <Text>I want to deposit </Text> 
  <Form>
            <Input
              type="number"
              value={props.amount}
              onChange={props.onChange}
              placeholder="i.e 100"
            ></Input>
  </Form>
  <Form>
          <Select name="token" id="currencies" className="icon-menu" >
          <Option >DAI</Option>
          <Option >ETH</Option>
          </Select>
        </Form>
<Text> and reinvest my earnings in </Text> 
</Container>
      </div>
    </Section>
  );
}

export default Conversation;
